'use strict';

const Semver = require('semver');

module.exports = function (payload, ecoSystemInterface) {

    const identity = payload.identity;
    if (Semver.satisfies(identity.version, '4.x')) {
        return require('./express_versions/express4')(payload, ecoSystemInterface);
    }
    /*else if (Semver.satisfies(identity.version, '2.x')) {
        require('./express/express2')(payload, ecoSystemInterface);
    }*/
    // else {
    //     ignore for now
    // }
};

module.exports.enable = function () {}; // noop for test coverage

