'use strict';

const Semver = require('semver');

module.exports = function (payload, ecoSystemInterface) {

    if (Semver.satisfies(payload.identity.version, '2.x')) {
        return require('./koa_versions/koa2')(payload, ecoSystemInterface);
    }
};

module.exports.enable = function () {}; // noop for test coverage
