'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const ConsumerTracing = require('../../../lib/tracing/consumer');

describe('Tracing', () => {

    describe('Producer', () => {

        it('should trace a producer', { plan: 1 }, (done) => {

            const res = ConsumerTracing({ transport: 'kafka', ip: '10.2.6.3', tracing_identifier: 'foo', host: 'hello', topic: 'test' });
            expect(res).to.equal({
                signal_name: 'tracing.consumer',
                payload_schema: 'tracing/consumer-2020-04-21',
                payload: {
                    transport: 'kafka',
                    host: 'hello',
                    topic: 'test',
                    ip: '10.2.6.3',
                    tracing_identifier: 'foo'
                }
            });
            done();
        });
    });
});
